exports.call_friends_by_id = function (id, friends, des, rate, callback) {
    user.find({token: id}, function(err, users) {
        if (users.length != 0) {
            var mUser = users[0];
            var called = true;
            for (var i = 0; i < friends.length; i++) {
                if (mUser.friends.indexOf(friends[i]) != -1) {
                    user.find({token: friends[i]}, function(err, users2) {
                        if (users2.length != 0) {
                            if (users2[0].gcm_token) {
                                console.log("Call " + friends[i]);
                                call_friend(mUser.username, users2[0].gcm_token, id, des, rate);
                                called = true;
                            } else {
                                console.log("Cannot call " + friends[i]);
                            }
                        } else {
                            console.log("Cannot find and ignore " + friends[i]);
                        }
                    });
                } else {
                    console.log(friends[i] + " is not a friend!");
                }
            }
            if (called) callback({ 'response': "Sending notifications to selected friends", 'res': true });
            else callback({ 'response': "Didnot send notifications to any friend", 'res': false });
        } else {
            callback({'response': "Cannot find user while call friends", 'res': false});
        }
    });
}

var call_friend = function (username, friend_token, room_id, des, rate) {
    request({
        url: 'https://gcm-http.googleapis.com/gcm/send',
        headers: {
            'Content-Type': 'application/json',
            Authorization: 'key=AIzaSyCXbMrUxAN_OUiF0kCJeEWPpgoxZw_5Imc'
        },
        method: 'POST',
        json: true,
        body: {
            "data": {
                sender: username,
                room_id: room_id,
                des: des,
                rate: rate,
                time: (new Date()).getTime
            },
            "to": friend_token
        }
    }, function () { });
};