var crypto = require('crypto');
var rand = require('csprng');
var mongoose = require('mongoose');
var nodemailer = require('nodemailer');
var user = require('config/models');

var generateCode = function() {
    return rand(31, 36);
}

var smtpTransport = nodemailer.createTransport("SMTP", {
    auth: {
        user: "guidemesfsu@gmail.com",
        pass: "sfsu2016spring"
    } 
});

var isEmailValid = function(email) {
    var re = /^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;
    return re.test(email);
}

var isPasswordValid = function(password) {
    return password.length >= 8;
} 

exports.login = function(email, password, callback) {  
    user.find({email: email}, function(err, users) {  
        if (users.length == 0) {
            callback({'response':"Email does not exist.", 'res':false, 'field':"email"});
        } else {  
            var salt = users[0].salt; 
            var hashed_password = users[0].hashed_password; 
            var token = users[0].token; 
            var temp = salt + password; 
            var hashed = crypto.createHash('sha512').update(temp).digest("hex"); 
            var username = users[0].username;
            var role = users[0].role;
            var code = users[0].invite_code;
            var rate = (Math.round((users[0].rateTotal / users[0].rateNum) * 10) / 10).toString();
            if (hashed == hashed_password) {
                callback({'response':"Login sucess.", 'res':true, 'token':token, 'username':username, 'role':role, 'rate':rate, 'invite_code': code});
            } else {  
                callback({'response':"Password is not correct.", 'res':false, 'field':"password"});  
            }
        } 
    }); 
} 

exports.register = function(email, password, username, role, callback) {  
    var x = email; 
    if (!isEmailValid(email)) {
        callback({'response':"Email is not valid.", 'res': false, 'field':"email"});
    } else {
        if (!isPasswordValid(password)) {
            callback({'response':"Password is too weak.", 'res': false, 'field':"password"});
        } else {
            var salt = rand(160, 36); 
            var temp = salt + password; 
            var token = crypto.createHash('sha512').update(email + salt).digest("hex"); 
            var hashed_password = crypto.createHash('sha512').update(temp).digest("hex");  
            var code = generateCode();

            user.find({email: email}, function(err, users) {  
                if (users.length != 0) {
                    callback({'response':"Email is already used.", 'res': false, 'field':"email"});
                } else {
                    var newuser = new user({    
                        token: token,   
                        email: email,   
                        hashed_password: hashed_password,   
                        salt: salt,
                        username: username,
                        role: role,
                        rateTotal: 5,
                        rateNum: 1,
                        gcm_token: '',
                        friends: [],
                        invite_code: code
                    });  
                    newuser.save(function(error) {
                        if (error) {
                            callback({'response':"Error while registering.", 'res': false, 'field':"none"});  
                        } else {
                            callback({'response':"Register success.", 'res': true});  
                        }
                    });
                }
            });
        }
    } 
}

